        "liveLectureDate" : [new Date(2021, 4, 8), new Date(2021, 4, 15), new Date(2021, 4, 22), new Date(2021, 4, 29),new Date(2021, 5, 5)],
        "lectureTime" : ["11:00-12:30","11:00-12:30","11:00-12:30","11:00-12:30","11:00-12:30"],
