              "liveLectureDate" : [new Date(2021, 7, 17), new Date(2021, 7, 24), new Date(2021, 7, 31), new Date(2021, 8, 7),new Date(2021, 8, 14)],
              "lectureTime" : ["20:00-21:30","20:00-21:45","20:00-21:45","20:00-21:45","20:00-21:45"],
              "minimumAtendee": 10,
              "price": 10000,
