              "liveLectureDate" : [new Date(2021, 9, 5), new Date(2021, 9, 12), new Date(2021, 9, 19), new Date(2021, 9, 26),new Date(2021, 10, 2)],
              "lectureTime" : ["20:00-21:30","20:00-21:45","20:00-21:45","20:00-21:45","20:00-21:45"],
