                  "liveLectureDate" : [new Date(2021, 10, 10), new Date(2021, 10, 17), new Date(2021, 10, 24), new Date(2021, 11, 1),new Date(2021, 11, 8)],
                  "lectureTime" : ["20:00-21:30","20:00-21:30","20:00-21:30","20:00-21:30","20:00-21:30"],
